document.addEventListener("DOMContentLoaded",()=>{let t=document.createElement("style");t.setAttribute("data-scroll-style",""),t.innerHTML="[data-scroll]{opacity:0;transform:translateY(100px);transition:opacity .8s ease-in-out,transform 1s cubic-bezier(.16, .88, .45, 1),-webkit-transform 1s cubic-bezier(.16, .88, .45, 1)}[data-scroll=in]{opacity:1;transform:none}",document.head.appendChild(t);let e=document.querySelectorAll('[data-scroll="out"]'),r=new IntersectionObserver((t,e)=>{t.forEach(t=>{if(t.isIntersecting){let r=t.target;r.setAttribute("data-scroll","in"),e.unobserve(r)}})},{rootMargin:"0px 0px -15% 0px"});e.forEach(t=>{r.observe(t)})});
